let derniereTouche="";window.addEventListener("keydown",(e=>{switch(e.key){case"z":touche.z.press=!0,derniereTouche="z";break;case"q":touche.q.press=!0,derniereTouche="q";break;case"s":touche.s.press=!0,derniereTouche="s";break;case"d":touche.d.press=!0,derniereTouche="d"}})),window.addEventListener("keyup",(e=>{switch(e.key){case"z":touche.z.press=!1;break;case"q":touche.q.press=!1;break;case"s":touche.s.press=!1;break;case"d":touche.d.press=!1}}));